import React, { useRef } from 'react';
import {
  InstantSearch,
  HierarchicalMenu,
  RefinementList,
  SortBy,
  Pagination,
  ClearRefinements,
  Highlight,
  Hits,
  HitsPerPage,
  Panel,
  Configure,
  SearchBox,
  Snippet,
  ToggleRefinement,
} from 'react-instantsearch-dom';
import algoliasearch from 'algoliasearch/lite';
import {
  ClearFiltersMobile,
  PriceSlider,
  NoResults,
  Ratings,
  ResultsNumberMobile,
  SaveFiltersMobile,
} from './widgets'
import withURLSync from './URLSync';
import { formatNumber } from './utils';
import './Theme.css';
import './App.css';
import './App.mobile.css';
import './widgets/Pagination.css';
// import AlgoliaSvg from './AlgoliaSvg';


const searchClient = algoliasearch(
  '1VWWQEFS9L',
  '09da229b366b4e16f36388091f0c0d3f'
);

const Hit = ({ hit }) => (
  <article className="hit">
    <header className="hit-image-container">
      <img src={hit.image_url} alt={hit.product_name} className="hit-image" />
    </header>

    <div className="hit-info-container">
      <p className="hit-category">{hit.category[0]}</p>
      <p className="hit-category">{hit.category[0]}</p>
      <p className="hit-category">{hit.colour}</p>
      <p className="hit-category">{hit.price}</p>
      <p className="hit-category">{hit.full_price}</p>
      <h1>
        <Highlight attribute="product_name" tagName="mark" hit={hit} />
      </h1>
      <p className="hit-description">
        <Snippet attribute="description" tagName="mark" hit={hit} />
      </p>

      <footer>
        <p>
          <span className="hit-em">$</span>{' '}
          <strong>{formatNumber(hit.price)}</strong>{' '}
          <span className="hit-em hit-rating">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="8"
              height="8"
              viewBox="0 0 16 16"
            >
              <path
                fill="#e2a400"
                fillRule="evenodd"
